package Java_Programs;

public class ComparisonOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//== , != , <, > , <=, >=

		int x = 10;
		int y = 5;
		//equalto
		System.out.println(x==y);
		//not equal
		System.out.println(x!=y);
		//lesser
		System.out.println(x<y);
		//lesser than equalto
		System.out.println(x<=y);
		//greater
		System.out.println(x>y);
		//greater than equalto
		System.out.println(x>=y);


	}

}
